mapper.update:
update organization
NAME = #{name,jdbcType=VARCHAR}, LOCATION = #{location.locationId,jdbcType=BIGINT}, PARENT_ID = #{parentId.organizationId,jdbcType=BIGINT}, CREATOR_ID = #{creatorId.userId,jdbcType=BIGINT},
where ORG_ID = #{orgId,jdbcType=BIGINT}

mapper.select:
SELECT *
FROM `file`
WHERE ASCRIPTION_TYPE = 'ORGANIZATION' AND organization = #{organizationID,jdbcType=BIGINT};

mapper.update:
UPDATE organization
SET organization.CREATOR_ID = #{userId,jdbcType=BIGINT}
WHERE organization.ORG_ID = #{orgId,jdbcType=BIGINT};

UPDATE organization
SET organization.PARENT_ID = #{parentId,jdbcType=BIGINT}
WHERE organization.ORG_ID = #{orgId,jdbcType=BIGINT};
